package strings;

import java.util.ArrayList;
import java.util.HashSet;
import java.util.List;

class FindAllAnagrams {
	List<Integer> l = new ArrayList<Integer>();

	public List<Integer> findAnagrams(String s, String p) {
		List<Integer> l = new ArrayList<>();
		HashSet<String> al = permutations("",p,new HashSet<String>());
		for(String word:al) {
			String tmp=s;
			int result=-1;
			l.addAll(findAnagramsFor(tmp,word));
		}
		return l;
	}

	public List<Integer> findAnagramsFor(String s, String p) {
		List<Integer> al= new ArrayList<Integer>();
		for (int i = 0; i < s.length(); i++) {
			if (s.substring(i).startsWith(p)) {
				al.add(i);
				i=i+p.length()-1;
			}
		}
		return al;
	}

	public HashSet<String> permutations(String p, String unp,HashSet<String> al){
		if(unp.isEmpty()) {
			al.add(p);
			return al;
		}
		
		for(int i=0;i<=p.length();i++) {
			permutations(p.substring(0,i)+unp.charAt(0)+p.substring(i), unp.substring(1), al);
		}
		return al;
	}

	public static void main(String[] args) {
		String s = "";
		String p = "yqrbgjdwtcaxzsnifvhmou";
	
		FindAllAnagrams obj = new FindAllAnagrams();
		List<Integer> al = obj.findAnagrams(s, p);
		System.out.println(al);

	}
}